import cv2 as cvimport numpy as npimg = np.zeros((480,640,3), np.uint8)cv.line(img, (20,20), (620, 460), (0, 255, 0), 3)  # 이미지, 시작점, 끝점, 컬러, 굵기cv.rectangle(img, (100,100), (400,400), (0,0,255), 3)cv.rectangle(img, (500,100), (600,200), (255,0,0),-1)cv.circle(img, (320,240), 100, (255,255,0), 3)# 타원 params = 이미지, 중심, (가로축길이, 세로축길이), 회전각, 시작각, 종료각, 컬러, 굵기cv.ellipse(img, (320,240), (300,200), 45, 0, 360, (0,255,255), 3)# 다각형 params = 이미지, 포인트(reshape 해야함), 닫힌 도형 여부, 컬러, 굵기pts = np.array([[50,150], [200,80], [350, 120], [300,200]], np.int32)   # 꼭지점 npcv.polylines(img, [pts.reshape((-1,1,2))], False, (255,0,255), 3)  # 꼭지점을 openCV에서 사용하기 위해 reshape(3차원으로)                                                                   # numpy reshape에서 -1은 자동# 속을 채운 다각형f_pts = np.array([[350,350], [500,280], [630,320], [520,320]], np.int32)cv.fillPoly(img, [f_pts.reshape((-1,1,2))], (0,0,255))# 텍스트 넣기 params = 이미지, 내용, 좌표, 폰트, 폰트크기, 컬러, 굵기cv.putText(img, 'Hello', (10,450), cv.FONT_HERSHEY_SCRIPT_SIMPLEX, 4, (255,255,255), 3)cv.imshow('img', img)cv.waitKey()